INSERT INTO `course` (`Seq`, `CourseName`, `CourseNumber`, `CourseImage`, `CourseImageType`, `StartTime`, `EndTime`, `FunctionCategory`, `TrainingGoal`, `OtherTrainingGoal`, `LecturerType`, `Lecturer`, `OtherLecturer`, `TrainingObject`, `OtherTrainingObject`, `OpenSignUp`, `AllowExternal`, `CourseSurvey1`, `CourseSurvey2`, `CourseSurvey3`, `CourseMemo`, `AuthorName`, `AuthorMemo`, `AuthorImage`, `AuthorImageType`, `CourseFocus`, `QuestionNums`, `CourseRelease`, `ReleaseDate`, `CreatDate`, `CreatUser`, `ModifyDate`, `ModifyUser`, `DeleteTag`) VALUES
	(1, '樂齡活化記憶樂齡手作', '2023010701', NULL, NULL, '', '', 5, 4, '', 2, 0, 5, 1, '', 1, 0, 1, 0, 0, '臺中市社區大學教師協會-白芳瑜老師【樂齡活化記憶樂齡手作】', NULL, '<p>白芳瑜老師出生於南投縣埔里鎮。從小見識母親進行服裝的製作，成長歲月耳濡目染，凡手工類都有濃厚興趣。科技大學學習功能性創作的學習，由淺入深領悟各類工藝。繼續不斷的進修各種專業學習，實踐活到老學到老的終身學習。</p>', NULL, NULL, '', 5, 1, '2023-01-07', '2023-01-07 13:31:14', 1, '2023-01-09 11:55:08', 1, 0),
	(2, '萬和宮老二媽西屯省親的故事', '2023010702', NULL, NULL, '', '', 1, 1, NULL, NULL, 0, 0, 1, NULL, 0, 0, 0, 0, 0, NULL, NULL, NULL, NULL, NULL, NULL, 5, 0, '', '2023-01-07 15:22:22', 1, '2023-01-09 11:42:44', 1, 1),
	(3, '萬和宮老二媽西屯省親的故事', '2023010901', '', 'data:image/jpeg;base64', '', '', 5, 4, '', 2, 0, 4, 1, '', 1, 0, 1, 0, 0, '臺中市社區大學教師協會-張昭友老師【萬和宮老二媽西屯省親的故事】', NULL, '<p>張昭友老師目前除了擔任臺中市社區大學教師協會理事長外，同時還擔任逢甲大學通識教育中心專業講師、台中市西屯區、北屯區與西區區公所社造中心的計畫主持人、台中市勞工局摘星計畫青創基地輔導委員、臺中市社區總體營造暨文化設施推動委員會委員及彤橙緣國際有限公司執行長。 &nbsp; &nbsp;目前積極推動社區營造工作培育社區居民自動自發、主動關心公共事務，透過社區資源的盤點與整合，讓居民學習如何發掘社區問題，面對問題和解決問題，經由課程與活動的辦理，增進社區居民對社區營造的認知與肯定；讓居民更認識社區。將社區營造的精神落實到轄內社區，讓社區居民有更多的機會來參與公共事務，從改造社區到改變人心。經由社造計畫的推行，增進社區居民間的互動，讓社區長輩的在地知識得到肯定與傳承，讓社區孩童能從活動中快樂學習，增添社區和諧、團結與歡樂的氛圍。</p>', NULL, NULL, '', 5, 1, '2023-01-09', '2023-01-09 11:15:17', 1, '2023-01-10 17:58:48', 1, 0),
	(4, '健身導引術', '2023010902', NULL, NULL, '', '', 5, 4, '', 2, 0, 9, 1, '', 1, 0, 1, 0, 0, '臺中市社區大學教師協會-曾榮伯老師【健身導引術】', NULL, '', NULL, NULL, '', 5, 1, '2023-01-09', '2023-01-09 11:53:24', 1, '2023-01-09 12:01:53', 1, 0),
	(5, '多肉植物創意組合盆栽', '2023011001', NULL, NULL, '', '', 5, 4, '', 2, 0, 6, 1, '', 1, 0, 1, 0, 0, '臺中市社區大學教師協會-陳慧真老師【多肉植物創意組合盆栽】', NULL, '', NULL, NULL, '', 5, 1, '2023-01-09', '2023-01-10 13:46:00', 1, '2023-01-10 13:47:24', 1, 0),
	(6, '彩色精油沐浴鹽課程', '2023011002', NULL, NULL, '', '', 5, 4, '', 2, 0, 7, 1, '', 1, 0, 1, 0, 0, '臺中市社區大學教師協會- 黃寶華老師【彩色精油沐浴鹽課程】', NULL, '', NULL, NULL, '', 5, 1, '2023-01-09', '2023-01-10 13:47:37', 1, NULL, NULL, 0),
	(7, '防疫精油噴霧DIY', '2023011003', NULL, NULL, '', '', 5, 4, '', 2, 0, 7, 1, '', 1, 0, 1, 0, 0, '臺中市社區大學教師協會-黃寶華老師【防疫精油噴霧DIY】', NULL, '', NULL, NULL, '', 5, 1, '2023-01-09', '2023-01-10 13:48:39', 1, NULL, NULL, 0),
	(8, '五行快樂吃輕鬆瘦', '2023011004', NULL, NULL, '', '', 5, 4, '', 2, 0, 8, 1, '', 1, 0, 1, 0, 0, '臺中市社區大學教師協會-黃麗容老師【五行快樂吃輕鬆瘦】', NULL, '<p>黃麗容老師是教紫微、八字、姓名學10多年的老師，<br />特別把八字五行，加上減重健康飲食方法結合，這個是世界首創的方法 !<br />協助大家可以快樂吃輕鬆瘦 !&nbsp;</p>', NULL, NULL, '', 5, 1, '2023-01-09', '2023-01-10 13:49:32', 1, NULL, NULL, 0),
	(9, '股票投資實務-黃金交叉', '2023011005', NULL, NULL, '', '', 5, 4, '', 2, 0, 10, 1, '', 1, 0, 1, 0, 0, '臺中市社區大學教師協會-黃火木老師【股票投資實務-黃金交叉】', NULL, '', NULL, NULL, '', 5, 1, '2023-01-09', '2023-01-10 13:51:28', 1, NULL, NULL, 0),
	(10, '轉吧，七彩的脈輪課程', '2023011006', '', 'image/jpeg', '', '', 5, 4, '', 2, 0, 11, 1, '', 1, 0, 1, 0, 0, '臺中市社區大學教師協會-蕭小絢老師【轉吧，七彩的脈輪課程】', NULL, '', NULL, NULL, '', 5, 1, '2023-01-09', '2023-01-10 13:52:11', 1, '2023-01-10 17:59:02', 1, 0);
